bool gs_psram_available = false;
size_t psramBytesTotal = 0;
size_t psramBytesFreeAtBoot = 0;

void *smartAlloc(size_t size) {
  if (gs_psram_available) {
    void *ptr = ps_malloc(size);
    if (ptr) return ptr;
  }
  return malloc(size);
}

template <typename T> struct PsramAllocator {
  using value_type = T;
  PsramAllocator() noexcept = default;
  template <class U> constexpr PsramAllocator(const PsramAllocator<U> &) noexcept {}
  [[nodiscard]] T *allocate(std::size_t n) {
    size_t bytes = n * sizeof(T);
    void *ptr = smartAlloc(bytes);
    if (!ptr) throw std::bad_alloc();
    return static_cast<T *>(ptr);
  }
  void deallocate(T *p, std::size_t) noexcept {
    if (p) free(p);
  }
};

template <class T, class U> bool operator==(const PsramAllocator<T> &, const PsramAllocator<U> &) { return true; }
template <class T, class U> bool operator!=(const PsramAllocator<T> &, const PsramAllocator<U> &) { return false; }

template <typename T> using PsramVector = std::vector<T, PsramAllocator<T>>;

