StatusColor vpdStatusColor(float currentVpd, float targetVpd) {
  if (!isfinite(currentVpd) || !isfinite(targetVpd)) return STATUS_RED;
  float delta = fabs(currentVpd - targetVpd);
  if (delta <= VPD_STATUS_OK_DELTA_KPA) return STATUS_GREEN;
  if (delta <= VPD_STATUS_WARN_DELTA_KPA) return STATUS_YELLOW;
  return STATUS_RED;
}

