void startCloudReconnect(const String &reason, bool immediate = true) {
  if (!cloudConfig.enabled || cloudConfig.baseUrl.length() == 0) return;
  cloudStatus.reconnectActive = true;
  cloudStatus.redState = false;
  cloudStatus.reconnectAttempts = 0;
  nextCloudRedRetryMs = 0;
  nextCloudReconnectAttemptMs = immediate ? millis() : (millis() + CLOUD_RECONNECT_INTERVAL_MS);
  cloudStatus.lastStateReason = reason;
  cloudStatus.lastStateChangeMs = currentEpochMs();
}

void logCloudRedFailure(const String &cause, const String &lastState) {
  String target = cloudStatus.lastPath.length() > 0 ? cloudStatus.lastPath
                  : (cloudStatus.lastUrl.length() > 0 ? cloudStatus.lastUrl
                     : (cloudStatus.lastUploadedPath.length() > 0 ? cloudStatus.lastUploadedPath : "n/a"));
  String status = String("connected=") + String(cloudStatus.connected ? 1 : 0) + " enabled=" + String(cloudStatus.enabled ? 1 : 0)
                  + " recording=" + String(cloudStatus.recording ? 1 : 0);
  String msg = "Cloud RED: cause=" + cause + " http=" + String(cloudStatus.lastHttpCode) + " error=\"" + cloudStatus.lastError
               + "\" last_state=\"" + lastState + "\" target=\"" + target + "\" status=" + status;
  logEvent(msg, "error", "cloud");
}

void refreshStorageMode(const String &reason = "") {
  bool runtimeActive = cloudConfig.enabled && cloudConfig.baseUrl.length() > 0;
  if (reason.length() > 0) {
    cloudStatus.lastStateReason = reason;
    cloudStatus.lastStateChangeMs = currentEpochMs();
  }
  if (!runtimeActive) {
    storageMode = StorageMode::LOCAL_ONLY;
  } else if (cloudHealthOk()) {
    storageMode = StorageMode::CLOUD_PRIMARY;
  } else {
    storageMode = StorageMode::LOCAL_FALLBACK;
  }
  cloudStatus.enabled = cloudConfig.enabled;
  cloudStatus.runtimeEnabled = runtimeActive;
  cloudStatus.connected = runtimeActive && cloudHealthOk();
  cloudStatus.recording = cloudConfig.recording;
  cloudStatus.deviceFolder = cloudRootPath();
}

uint16_t retentionDays() {
  uint8_t months = cloudConfig.retentionMonths;
  if (months < 1) months = 1;
  if (months > 6) months = 6;
  return (uint16_t)(months * 30);
}

void markCloudSuccess(const String &reason = "cloud ok") {
  lastCloudOkMs = millis();
  lastCloudOkEpochMs = currentEpochMs();
  cloudStatus.lastPingMs = lastCloudOkEpochMs;
  cloudStatus.lastError = "";
  cloudStatus.lastErrorSuffix = "";
  clearCloudReconnectState();
  cloudStatus.lastStateReason = reason;
  cloudStatus.lastStateChangeMs = currentEpochMs();
  refreshStorageMode(reason);
}

void markCloudFailure(const String &msg) {
  lastCloudOkMs = 0;
  cloudStatus.lastError = msg;
  cloudStatus.lastErrorSuffix = "";
  cloudStatus.failureCount++;
  cloudStatus.lastFailureMs = currentEpochMs();
  cloudStatus.connected = false;
  cloudStatus.lastStateReason = msg;
  cloudStatus.lastStateChangeMs = currentEpochMs();
  logEvent("Cloud error: " + msg, "error", "cloud");
  if (cloudConfig.enabled && cloudConfig.baseUrl.length() > 0 && !cloudStatus.redState && !cloudStatus.reconnectActive) {
    startCloudReconnect("auto reconnect", false);
  }
  refreshStorageMode(msg);
}

void attemptCloudReconnect() {
  if (!cloudStatus.reconnectActive || cloudStatus.redState) return;
  if (!cloudStatus.runtimeEnabled) return;
  unsigned long now = millis();
  if (nextCloudReconnectAttemptMs != 0 && now < nextCloudReconnectAttemptMs) return;
  if (cloudStatus.reconnectAttempts >= CLOUD_RECONNECT_MAX_ATTEMPTS) {
    String prevReason = cloudStatus.lastStateReason;
    cloudStatus.redState = true;
    cloudStatus.reconnectActive = false;
    cloudStatus.lastStateReason = "reconnect failed";
    cloudStatus.lastStateChangeMs = currentEpochMs();
    nextCloudRedRetryMs = millis() + CLOUD_RED_RETRY_INTERVAL_MS;
    logCloudRedFailure("reconnect failed", prevReason);
    return;
  }
  cloudStatus.reconnectAttempts++;
  bool ok = pingCloud(true);
  if (ok) {
    markCloudSuccess("reconnect ok");
    return;
  }
  if (cloudStatus.reconnectAttempts >= CLOUD_RECONNECT_MAX_ATTEMPTS) {
    String prevReason = cloudStatus.lastStateReason;
    cloudStatus.redState = true;
    cloudStatus.reconnectActive = false;
    cloudStatus.lastStateReason = "reconnect failed";
    cloudStatus.lastStateChangeMs = currentEpochMs();
    nextCloudRedRetryMs = millis() + CLOUD_RED_RETRY_INTERVAL_MS;
    logCloudRedFailure("reconnect failed", prevReason);
    return;
  }
  nextCloudReconnectAttemptMs = now + CLOUD_RECONNECT_INTERVAL_MS;
  cloudStatus.lastStateReason = "reconnect retry";
  cloudStatus.lastStateChangeMs = currentEpochMs();
}

