void appendWifiNetworksJson(String &json) {
  for (size_t i = 0; i < wifiScanResults.size(); i++) {
    const auto &entry = wifiScanResults[i];
    json += "{\"ssid\":\"" + jsonEscape(entry.ssid) + "\",\"rssi\":" + String(entry.rssi);
    json += ",\"auth\":\"" + String(wifiAuthLabel(entry.auth)) + "\"";
    json += ",\"bssid\":\"" + jsonEscape(entry.bssid) + "\"";
    json += ",\"channel\":" + String(entry.channel) + "}";
    if (i + 1 < wifiScanResults.size()) json += ",";
  }
}

bool startWifiScan() {
  if (wifiScan.scanning) return false;
  wifiScan.lastStartAttemptMs = millis();
  wifiScan.scanning = true;
  wifiScan.startedAtMs = millis();
  wifiScan.lastCompletedAtMs = 0;
  wifiScan.lastResultCount = 0;
  wifiScan.lastError = "";
  wifiScanResults.clear();
  WiFi.scanDelete();
  int res = WiFi.scanNetworks(true, true);
  if (res == WIFI_SCAN_FAILED) {
    wifiScan.scanning = false;
    wifiScan.lastError = "failed";
    wifiScan.lastCompletedAtMs = millis();
    return false;
  }

  // Single-request scan with bounded timeout to avoid long blocking.
  unsigned long deadline = millis() + WIFI_SCAN_TIMEOUT_MS;
  while (res == WIFI_SCAN_RUNNING && millis() < deadline) {
    delay(50);
    res = WiFi.scanComplete();
    if (res == WIFI_SCAN_FAILED) {
      wifiScan.scanning = false;
      wifiScan.lastError = "failed";
      wifiScan.lastCompletedAtMs = millis();
      WiFi.scanDelete();
      return false;
    }
  }

  if (res == WIFI_SCAN_RUNNING) {
    wifiScan.scanning = false;
    wifiScan.lastResultCount = 0;
    wifiScan.lastError = "timeout";
    wifiScan.lastCompletedAtMs = millis();
    WiFi.scanDelete();
    return false;
  }

  if (res >= 0) {
    wifiScanResults.clear();
    wifiScanResults.reserve(res);
    for (int i = 0; i < res; i++) {
      WifiScanNetwork entry;
      entry.ssid = WiFi.SSID(i);
      if (entry.ssid.length() == 0) continue;
      entry.rssi = WiFi.RSSI(i);
      entry.auth = WiFi.encryptionType(i);
      entry.bssid = WiFi.BSSIDstr(i);
      entry.channel = WiFi.channel(i);
      wifiScanResults.push_back(entry);
    }
    wifiScan.scanning = false;
    wifiScan.lastResultCount = wifiScanResults.size();
    wifiScan.lastCompletedAtMs = millis();
    wifiScan.lastError = "";
    WiFi.scanDelete();
    return true;
  }

  wifiScan.scanning = false;
  wifiScan.lastError = "failed";
  wifiScan.lastCompletedAtMs = millis();
  WiFi.scanDelete();
  return false;
}

void wifiScanTick() {
  if (!wifiScan.scanning) return;
  int n = WiFi.scanComplete();
  if (n == WIFI_SCAN_FAILED) {
    wifiScan.scanning = false;
    wifiScan.lastError = "failed";
    wifiScan.lastCompletedAtMs = millis();
    wifiScan.lastResultCount = 0;
    WiFi.scanDelete();
    return;
  }
  if (n >= 0) {
    wifiScanResults.clear();
    wifiScanResults.reserve(n);
    for (int i = 0; i < n; i++) {
      WifiScanNetwork entry;
      entry.ssid = WiFi.SSID(i);
      if (entry.ssid.length() == 0) continue;
      entry.rssi = WiFi.RSSI(i);
      entry.auth = WiFi.encryptionType(i);
      entry.bssid = WiFi.BSSIDstr(i);
      entry.channel = WiFi.channel(i);
      wifiScanResults.push_back(entry);
    }
    wifiScan.scanning = false;
    wifiScan.lastResultCount = wifiScanResults.size();
    wifiScan.lastCompletedAtMs = millis();
    wifiScan.lastError = "";
    WiFi.scanDelete();
    return;
  }
  if (millis() - wifiScan.startedAtMs > WIFI_TIMEOUT) {
    wifiScan.scanning = false;
    wifiScan.lastResultCount = 0;
    wifiScan.lastError = "timeout";
    wifiScan.lastCompletedAtMs = millis();
    WiFi.scanDelete();
  }
}

void applyIntegrationMode(IntegrationMode mode, bool persist) {
  integrationMode = mode;
  piBridge.enabled = (integrationMode == IntegrationMode::PI_BRIDGE);
  if (!piBridge.enabled) {
    piBridge.detected = false;
    piBridge.active = false;
    piBridge.lastHandshakeMs = 0;
    piBridge.lastForwardMs = 0;
    piBridge.lastDataMs = 0;
    piBridgeWifiSent = false;
    piBridgeDataStreaming = false;
    piBridgeRxBuffer = "";
    uiDisableActive = false;
  }
  if (!persist) return;
  prefsSystem.begin("system", false);
  prefsSystem.putUChar("integration_mode", static_cast<uint8_t>(integrationMode));
  prefsSystem.end();
}

void sendWifiPayloadToPi() {
  if (!piBridge.enabled || piBridgeWifiSent) return;
  String ssid = jsonEscape(savedSsid);
  String pass = jsonEscape(savedWifiPass);
  String ip = (apMode ? WiFi.softAPIP() : WiFi.localIP()).toString();
  String gw = (staticIpEnabled ? staticGateway : WiFi.gatewayIP()).toString();
  String sn = (staticIpEnabled ? staticSubnet : WiFi.subnetMask()).toString();
  String payload = "{\"type\":\"wifi\",\"ssid\":\"" + ssid + "\",\"password\":\"" + pass + "\",\"ip\":\"" + ip +
                   "\",\"gw\":\"" + jsonEscape(gw) + "\",\"sn\":\"" + jsonEscape(sn) + "\"}";
  piSerial.println(payload);
  piBridgeWifiSent = true;
  appendPiLog("WLAN-Credentials an Pi gesendet");
}

void forwardTelemetryToPi() {
  if (!piBridge.active) return;
  uint64_t ts = currentEpochMs();
  char payload[256];
  snprintf(payload, sizeof(payload),
           "{\"ts\":%llu,\"temp\":%.1f,\"rh\":%.1f,\"vpd\":%.3f,\"lux\":%.1f,\"ppfd\":%.1f,\"co2\":%d}",
           static_cast<unsigned long long>(ts),
           safeFloat(latest.ambientTempC, NAN), safeFloat(latest.humidity, NAN), safeFloat(latest.vpd, NAN),
           safeFloat(latest.lux, NAN), safeFloat(latest.ppfd, NAN), safeInt(latest.co2ppm, -1));
  piSerial.println(payload);
  piBridge.lastForwardMs = millis();
}

void tickPiBridge() {
  if (!piBridge.enabled) return;
  unsigned long now = millis();
  while (piSerial.available() > 0) {
    char c = static_cast<char>(piSerial.read());
    if (c == '\n') {
      String line = piBridgeRxBuffer;
      piBridgeRxBuffer = "";
      line.trim();
      if (line == "HELLO_GROWPI") {
        piBridge.detected = true;
        piBridge.active = true;
        piBridge.lastHandshakeMs = now;
        piBridge.lastDataMs = now;
        piBridgeWifiSent = false;
        piBridgeDataStreaming = false;
        appendPiLog("Handshake erfolgreich");
        sendWifiPayloadToPi();
      } else if (line.length() > 0) {
        piBridge.lastDataMs = now;
        if (!piBridgeDataStreaming) {
          piBridgeDataStreaming = true;
          appendPiLog("Datenstream läuft");
        }
      }
    } else if (c != '\r') {
      if (piBridgeRxBuffer.length() < 120) {
        piBridgeRxBuffer += c;
      }
    }
  }

  if (now - lastPiHandshakeAttemptMs > PI_BRIDGE_HANDSHAKE_INTERVAL_MS) {
    piSerial.println("HELLO_GROWSENSOR");
    lastPiHandshakeAttemptMs = now;
  }
  if (piBridge.lastHandshakeMs != 0 && now - piBridge.lastHandshakeMs > PI_BRIDGE_HANDSHAKE_STALE_MS) {
    piBridge.active = false;
    piBridge.detected = false;
    piBridgeWifiSent = false;
    piBridgeDataStreaming = false;
    piBridge.lastDataMs = 0;
    appendPiLog("Handshake verloren");
  }
}

void checkPiBridgeFailover() {
  if (!uiDisableRequested || !piBridge.active) return;
  if (piBridge.lastDataMs == 0) return;
  unsigned long now = millis();
  if (now - piBridge.lastDataMs <= PI_BRIDGE_FAILOVER_TIMEOUT_MS) return;
  piBridge.active = false;
  piBridge.detected = false;
  piBridgeWifiSent = false;
  piBridgeDataStreaming = false;
  piBridge.lastDataMs = 0;
  lastPiFailoverMs = currentEpochMs();
  appendPiLog("Pi Kommunikation verloren – ESP wieder aktiv", "warn");
}

void updateUiDisableState() {
  const bool shouldDisable = uiDisableRequested && piBridge.active;
  if (shouldDisable == uiDisableActive) return;
  uiDisableActive = shouldDisable;
  appendPiLog(uiDisableActive ? "ESP-WebGUI deaktiviert (Pi aktiv)" : "ESP-WebGUI wieder aktiv");
}

