// TEST: WiFi-Scan starten -> UI bleibt responsive, ESP friert nicht ein.
// TEST: WebUI neu laden -> Header + Kacheln füllen sich ohne Fehlflut.
// TEST: Mobile (kleines Viewport) -> Header OK, VPD-Kachel zeigt Chart, kein blaues Vollfeld.
// TEST: Cloud offline -> UI bleibt performant, Toast + Status-LED rot.
void loop() {
  static uint32_t lastLoopMicros = 0;
  uint32_t loopNow = micros();
  if (lastLoopMicros != 0) {
    lastLoopDurationMs = (loopNow - lastLoopMicros) / 1000UL;
  }
  lastLoopMicros = loopNow;
  if (apMode) {
    dnsServer.processNextRequest();
  }
  pruneLogsIfLowMemory(false);
  server.handleClient();
  maintainWifiConnection();
  wifiScanTick();
  maintainTimeSync();
  tickPiBridge();
  checkPiBridgeFailover();
  updateUiDisableState();
  if (piBridge.active && millis() - piBridge.lastForwardMs > PI_BRIDGE_FORWARD_INTERVAL_MS) {
    forwardTelemetryToPi();
  }

  if (millis() - lastSensorMillis >= SENSOR_INTERVAL) {
    lastSensorMillis = millis();
    readSensors();
    applyVpdRelays();
  }
}
